import React from 'react';

const EmotionalLandscape = () => {
  const emotions = [
    { name: 'Anger', ukr: 45.9, rus: 76.0, color: '#E60000', total: 121.9 },
    { name: 'Sadness', ukr: 11.3, rus: 3.8, color: '#4B8BE8', total: 15.1 },
    { name: 'Optimism', ukr: 10.1, rus: 6.5, color: '#BE5EBF', total: 16.6 },
    { name: 'Joy', ukr: 8.8, rus: 2.8, color: '#FFD93D', total: 11.6 },
    { name: 'Mix', ukr: 23.8, rus: 10.9, color: '#62BB47', total: 34.7 }
  ];

  const maxPercentage = 80;
  const gapWidth = 85;

  const getOpacity = (value1, value2) => {
    return value1 > value2 ? 0.9 : 0.5;
  };

  return (
    <div className="w-full max-w-4xl bg-white rounded-lg shadow-2xl">
      <div className="border-b border-[#E1E8ED] p-6 bg-gradient-to-b from-[#F5F8FA] to-white">
        <h2 className="text-3xl font-bold text-[#14171A] mb-4 text-center">
          Sentiments of War
        </h2>
        <p className="text-[#657786] text-center text-lg">
          A visual exploration of emotional expressions on Twitter in war time (Feb - Jun 2022)
        </p>
      </div>
      <div className="p-6">
        <div className="flex flex-col gap-8">
          <div className="relative h-[500px] w-full">
            {/* Labels at the top */}
            <div className="absolute top-4 left-0 right-0 flex justify-between px-8 text-lg font-bold">
              <span className="text-[#1DA1F2]">Pro-Ukrainian Posts</span>
              <span className="text-[#DC2626]">Pro-Russian Posts</span>
            </div>

            {emotions.map((emotion, index) => {
              const yPosition = 100 + (index * 80);
              const ukrWidth = (emotion.ukr / maxPercentage) * 40;
              const rusWidth = (emotion.rus / maxPercentage) * 40;

              return (
                <div key={emotion.name} className="absolute w-full" style={{ top: yPosition }}>
                  {/* Ukrainian side with gap */}
                  <div className="absolute right-1/2 w-[42%] flex justify-end items-center" 
                       style={{ right: `calc(50% + ${gapWidth/2}px)` }}>
                    <div className="relative group">
                      <div 
                        className="h-16 rounded-lg transition-all duration-500 hover:scale-y-110 flex items-center justify-end px-4"
                        style={{
                          width: `${ukrWidth}vw`,
                          backgroundColor: emotion.color,
                          opacity: getOpacity(emotion.ukr, emotion.rus)
                        }}
                      >
                        <span className="text-white font-bold text-lg drop-shadow-lg">
                          {emotion.ukr}%
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Emotion label - Centered */}
                  <div className="absolute left-1/2 transform -translate-x-1/2 
                                flex items-center justify-center h-16">
                    <div className="text-base font-bold px-3 py-1 rounded-full
                                  whitespace-nowrap"
                         style={{ color: emotion.color }}>
                      {emotion.name}
                    </div>
                  </div>

                  {/* Russian side with gap */}
                  <div className="absolute w-[42%] flex items-center" 
                       style={{ left: `calc(50% + ${gapWidth/2}px)` }}>
                    <div className="relative group">
                      <div 
                        className="h-16 rounded-lg transition-all duration-500 hover:scale-y-110 flex items-center px-4"
                        style={{
                          width: `${rusWidth}vw`,
                          backgroundColor: emotion.color,
                          opacity: getOpacity(emotion.rus, emotion.ukr)
                        }}
                      >
                        <span className="text-white font-bold text-lg drop-shadow-lg">
                          {emotion.rus}%
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Legend/Key Insights */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
            <div className="bg-[#F5F8FA] p-6 rounded-lg border border-[#E1E8ED]">
              <h4 className="text-[#1DA1F2] font-bold text-xl mb-3">Pro-Ukrainian Perspective</h4>
              <p className="text-[#657786] text-base leading-relaxed">
                A more distributed emotional spectrum, with anger tempered by mix of other sentiments. 
                Higher presence of optimism suggests resilience.
              </p>
            </div>
            <div className="bg-[#F5F8FA] p-6 rounded-lg border border-[#E1E8ED]">
              <h4 className="text-[#DC2626] font-bold text-xl mb-3">Pro-Russian Narrative</h4>
              <p className="text-[#657786] text-base leading-relaxed">
                Dominated by intense anger, with minimal expression of other emotions. 
                Shows a more uniform emotional response.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmotionalLandscape;
