import React from 'react';

const EmotionalLandscape = () => {
  const emotions = [
    { name: 'Anger', ukr: 46, rus: 76.0, color: '#E60000', total: 121.9 },
    { name: 'Sadness', ukr: 11, rus: 4, color: '#4B8BE8', total: 15.1 },
    { name: 'Optimism', ukr: 10, rus: 6, color: '#BE5EBF', total: 16.6 },
    { name: 'Joy', ukr: 9, rus: 3, color: '#FFD93D', total: 11.6 },
    { name: 'Mix', ukr: 24, rus: 11, color: '#62BB47', total: 34.7 }
  ];

  const maxPercentage = 80;
  const gapWidth = 100;
  const barWidth = 30;

  const getOpacity = (value1, value2) => {
    return value1 > value2 ? 0.9 : 0.5;
  };

  return (
    <div className="w-full max-w-5xl bg-white rounded-lg shadow-2xl mx-auto my-8">
      <div className="border-b border-[#E1E8ED] p-4 bg-gradient-to-b from-[#F5F8FA] to-white">
        <h2 className="text-3xl font-bold text-[#14171A] mb-2 text-center">
          Sentiments of War
        </h2>
        <p className="text-[#657786] text-center text-lg">
          Emotional expressions on Twitter during the first months of full-scale Russian invasion in Ukraine (Feb - Jun 2022)
        </p>
      </div>
      <div className="p-4">
        <div className="flex flex-col gap-6">
          <div className="relative h-[500px] w-full flex">
            {/* Left side text box */}
            <div className="absolute left-[22%] top-0 -translate-x-1/2 w-[480px]">
              <div className="bg-[#F5F8FA] p-3 rounded-lg border border-[#E1E8ED] text-center">
                <h4 className="text-[#1DA1F2] font-bold text-base mb-2">Pro-Ukrainian Posts</h4>
                <p className="text-[#657786] text-sm leading-relaxed">
                  A more distributed emotional spectrum, with anger tempered by mix of other sentiments. 
                  Higher presence of optimism suggests resilience.
                </p>
              </div>
            </div>

            {/* Right side text box */}
            <div className="absolute right-[22%] top-0 translate-x-1/2 w-[450px]">
              <div className="bg-[#F5F8FA] p-3 rounded-lg border border-[#E1E8ED] text-center">
                <h4 className="text-[#DC2626] font-bold text-base mb-2">Pro-Russian Posts</h4>
                <p className="text-[#657786] text-sm leading-relaxed">
                  Dominated by intense anger, with minimal expression of other emotions. 
                  Shows a more uniform emotional response.
                </p>
              </div>
            </div>

            {/* Center visualization */}
            <div className="flex-1 relative mt-20">
              {emotions.map((emotion, index) => {
                const yPosition = 30 + (index * 80);
                const ukrWidth = (emotion.ukr / maxPercentage) * barWidth;
                const rusWidth = (emotion.rus / maxPercentage) * barWidth;

                return (
                  <div key={emotion.name} className="absolute w-full" style={{ top: yPosition }}>
                    {/* Ukrainian side with gap */}
                    <div className="absolute right-0 w-[42%] flex justify-end items-center" 
                         style={{ right: `calc(50% + ${gapWidth/2}px)` }}>
                      <div className="relative group flex items-center justify-end">
                        <span className="font-bold text-base mr-4" 
                              style={{ color: emotion.color }}>
                          {emotion.ukr}%
                        </span>
                        <div 
                          className="h-16 rounded-lg transition-all duration-500 hover:scale-y-110"
                          style={{
                            width: `${ukrWidth}vw`,
                            backgroundColor: emotion.color,
                            opacity: getOpacity(emotion.ukr, emotion.rus)
                          }}
                        />
                      </div>
                    </div>

                    {/* Emotion label - Centered */}
                    <div className="absolute left-1/2 transform -translate-x-1/2 
                                  flex items-center justify-center h-16">
                      <div className="text-lg font-bold px-4 py-2 rounded-full
                                    whitespace-nowrap"
                           style={{ color: emotion.color }}>
                        {emotion.name}
                      </div>
                    </div>

                    {/* Russian side with gap */}
                    <div className="absolute w-[42%] flex items-center" 
                         style={{ left: `calc(50% + ${gapWidth/2}px)` }}>
                      <div className="relative group flex items-center">
                        <div 
                          className="h-16 rounded-lg transition-all duration-500 hover:scale-y-110"
                          style={{
                            width: `${rusWidth}vw`,
                            backgroundColor: emotion.color,
                            opacity: getOpacity(emotion.rus, emotion.ukr)
                          }}
                        />
                        <span className="font-bold text-base ml-4"
                              style={{ color: emotion.color }}>
                          {emotion.rus}%
                        </span>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmotionalLandscape;
