import React from 'react';

const EmotionalLandscape = () => {
  // Colors inspired by Inside Out:
  // Anger - Red (more vibrant)
  // Joy - Bright Yellow
  // Sadness - Deep Blue
  // Mix/Disgust - Green
  // Optimism/Fear - Purple
  const emotions = [
    { name: 'Anger', ukr: 46.9, rus: 76.0, color: '#E60000' },  // Bright red
    { name: 'Mix', ukr: 23.8, rus: 10.9, color: '#62BB47' },    // Bright green
    { name: 'Sadness', ukr: 11.3, rus: 3.8, color: '#4B8BE8' }, // Bright blue
    { name: 'Joy', ukr: 8.8, rus: 2.8, color: '#FFD93D' },      // Bright yellow
    { name: 'Optimism', ukr: 10.1, rus: 6.5, color: '#BE5EBF' } // Bright purple
  ];

  const maxPercentage = 80;

  return (
    <div className="w-full max-w-4xl bg-gradient-to-b from-gray-900 to-black rounded-lg shadow-2xl">
      <div className="border-b border-gray-700 p-6">
        <h2 className="text-3xl font-bold text-white mb-4 text-center">
          Emotional Echoes of War
        </h2>
        <p className="text-gray-300 text-center text-lg">
          A visual exploration of emotional expressions in wartime social media
        </p>
      </div>
      <div className="p-6">
        <div className="flex flex-col gap-8">
          <div className="relative h-[500px] w-full">
            {/* Vertical divider */}
            <div className="absolute top-0 left-1/2 h-full w-px bg-gray-700 transform -translate-x-1/2" />

            {/* Labels at the top */}
            <div className="absolute top-4 left-0 right-0 flex justify-between px-8 text-lg font-bold">
              <span className="text-blue-300">Pro-Ukrainian Posts</span>
              <span className="text-red-300">Pro-Russian Posts</span>
            </div>

            {emotions.map((emotion, index) => {
              const yPosition = 100 + (index * 80);
              const ukrWidth = (emotion.ukr / maxPercentage) * 45;
              const rusWidth = (emotion.rus / maxPercentage) * 45;

              return (
                <div key={emotion.name} className="absolute w-full" style={{ top: yPosition }}>
                  {/* Emotion label */}
                  <div className="absolute left-1/2 transform -translate-x-1/2 -translate-y-4 
                                text-base font-bold bg-gray-900 px-3 py-1 rounded-full z-10"
                       style={{ color: emotion.color }}>
                    {emotion.name}
                  </div>

                  {/* Ukrainian side */}
                  <div className="absolute right-1/2 w-[45%] flex justify-end items-center">
                    <div className="relative group">
                      <div 
                        className="h-16 rounded-l-full transition-all duration-500 hover:scale-y-110 flex items-center justify-end px-4"
                        style={{
                          width: `${ukrWidth}vw`,
                          backgroundColor: emotion.color,
                          opacity: 0.8
                        }}
                      >
                        <span className="text-white font-bold text-lg">
                          {emotion.ukr}%
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Russian side */}
                  <div className="absolute left-1/2 w-[45%] flex items-center">
                    <div className="relative group">
                      <div 
                        className="h-16 rounded-r-full transition-all duration-500 hover:scale-y-110 flex items-center px-4"
                        style={{
                          width: `${rusWidth}vw`,
                          backgroundColor: emotion.color,
                          opacity: 0.8
                        }}
                      >
                        <span className="text-white font-bold text-lg">
                          {emotion.rus}%
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Legend/Key Insights */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
            <div className="bg-gray-800 bg-opacity-50 p-6 rounded-lg border border-gray-700">
              <h4 className="text-blue-300 font-bold text-xl mb-3">Western Perspective</h4>
              <p className="text-gray-300 text-base leading-relaxed">
                A more distributed emotional spectrum, with anger tempered by mix of other sentiments. 
                Higher presence of optimism suggests resilience.
              </p>
            </div>
            <div className="bg-gray-800 bg-opacity-50 p-6 rounded-lg border border-gray-700">
              <h4 className="text-red-300 font-bold text-xl mb-3">Eastern Narrative</h4>
              <p className="text-gray-300 text-base leading-relaxed">
                Dominated by intense anger, with minimal expression of other emotions. 
                Shows a more uniform emotional response.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmotionalLandscape;
