import React from 'react';
import { Card, CardHeader, CardTitle, CardContent } from "/components/ui/card"


const EmotionalLandscape = () => {
  const emotions = [
    { name: 'Anger', ukr: 46.9, rus: 76.0, color: '#FF4444' },
    { name: 'Mix', ukr: 23.8, rus: 10.9, color: '#A78BFA' },
    { name: 'Sadness', ukr: 11.3, rus: 3.8, color: '#4B5563' },
    { name: 'Joy', ukr: 8.8, rus: 2.8, color: '#FCD34D' },
    { name: 'Optimism', ukr: 10.1, rus: 6.5, color: '#34D399' }
  ];

  const maxPercentage = 80; // For scaling

  return (
    <Card className="w-full max-w-4xl bg-gradient-to-b from-slate-900 to-slate-800">
      <CardHeader className="border-b border-slate-700">
        <CardTitle className="text-2xl font-bold text-white mb-4">
          Emotional Echoes of War
        </CardTitle>
        <p className="text-slate-300">
          A visual exploration of emotional expressions in wartime social media
        </p>
      </CardHeader>
      <CardContent className="p-6">
        <div className="flex flex-col gap-8">
          {/* Main visualization area */}
          <div className="relative h-[500px] w-full">
            {/* Vertical divider */}
            <div className="absolute top-0 left-1/2 h-full w-px bg-slate-700 transform -translate-x-1/2" />

            {/* Label at the top */}
            <div className="absolute top-4 left-0 right-0 flex justify-between px-8 text-sm">
              <span className="text-blue-300">Pro-Ukrainian Posts</span>
              <span className="text-red-300">Pro-Russian Posts</span>
            </div>

            {emotions.map((emotion, index) => {
              const yPosition = 100 + (index * 80);
              const ukrWidth = (emotion.ukr / maxPercentage) * 45;
              const rusWidth = (emotion.rus / maxPercentage) * 45;

              return (
                <div key={emotion.name} className="absolute w-full" style={{ top: yPosition }}>
                  {/* Emotion label */}
                  <div className="absolute left-1/2 transform -translate-x-1/2 -translate-y-4 
                                text-sm font-medium text-slate-400 bg-slate-800 px-2 z-10">
                    {emotion.name}
                  </div>

                  {/* Ukrainian side */}
                  <div className="absolute right-1/2 w-[45%] flex justify-end items-center">
                    <div className="relative group">
                      <div 
                        className="h-12 rounded-l-full transition-all duration-500 hover:scale-y-110"
                        style={{
                          width: `${ukrWidth}vw`,
                          backgroundColor: emotion.color,
                          opacity: emotion.ukr / 100 + 0.3
                        }}
                      />
                      {/* Tooltip */}
                      <div className="absolute -top-8 right-0 bg-slate-900 text-white text-xs px-2 py-1 rounded 
                                    opacity-0 group-hover:opacity-100 transition-opacity">
                        {emotion.ukr}%
                      </div>
                    </div>
                  </div>

                  {/* Russian side */}
                  <div className="absolute left-1/2 w-[45%] flex items-center">
                    <div className="relative group">
                      <div 
                        className="h-12 rounded-r-full transition-all duration-500 hover:scale-y-110"
                        style={{
                          width: `${rusWidth}vw`,
                          backgroundColor: emotion.color,
                          opacity: emotion.rus / 100 + 0.3
                        }}
                      />
                      {/* Tooltip */}
                      <div className="absolute -top-8 left-0 bg-slate-900 text-white text-xs px-2 py-1 rounded 
                                    opacity-0 group-hover:opacity-100 transition-opacity">
                        {emotion.rus}%
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Legend/Key Insights */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-8">
            <div className="bg-slate-700 bg-opacity-30 p-4 rounded-lg">
              <h4 className="text-blue-300 font-semibold mb-2">Western Perspective</h4>
              <p className="text-slate-300 text-sm">
                A more distributed emotional spectrum, with anger tempered by mix of other sentiments. 
                Higher presence of optimism suggests resilience.
              </p>
            </div>
            <div className="bg-slate-700 bg-opacity-30 p-4 rounded-lg">
              <h4 className="text-red-300 font-semibold mb-2">Eastern Narrative</h4>
              <p className="text-slate-300 text-sm">
                Dominated by intense anger, with minimal expression of other emotions. 
                Shows a more uniform emotional response.
              </p>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default EmotionalLandscape;
